import React, { useState, useEffect } from 'react';

import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import { Button } from '@material-ui/core';
import { useHistory } from "react-router-dom";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';


import api from '../apiCalls';
// import deleteRow from './Delete';

const deleteClick = async (ID) => {

  const done = await api.deleteJha(ID); 
  if (done) {
    console.log('Complete');
  }

  window.location.reload();
}



const useRowStyles = makeStyles({
    root: {
      '& > *': {
        borderBottom: 'unset',
      },
    },
  });





function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);
    const classes = useRowStyles();
    const history = useHistory();

    const routeChange = (id) =>{ 
      let path = `/edit/`+id; 
      history.push(path);
    }

    return (
      <React.Fragment>
        <TableRow className={classes.root}>
          <TableCell>
            <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton>
          </TableCell>
          <TableCell component="th" scope="row">
            {row.job}
          </TableCell>
          <TableCell align="right">{row.title}</TableCell>
          <TableCell align="right">{row.organization}</TableCell>
          <TableCell align="right">{row.ppe}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <Box margin={1}>
                <Table size="small" aria-label="moreInfo">
                  <TableHead>
                    <TableRow>
                      <TableCell>Date</TableCell>
                      <TableCell align="right">New/Revised</TableCell> 
                      {/* I just realized. this new/revised 
                      isnt needed in an online app*/}
                      <TableCell align="right">Supervisor</TableCell> 
                      <TableCell align="right">Analyst</TableCell> 
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableRow>
                      <TableCell component="th" scope="row">
                        {row.selectedDate}
                      </TableCell>
                      <TableCell align="right">{row.status}</TableCell>
                      <TableCell align="right">{row.supervisor}</TableCell>
                      <TableCell align="right">{row.analyst}</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>

                <Table size="small" aria-label="evenMoreInfo">
                  <TableHead>
                    <TableRow>
                      <TableCell>Location</TableCell>
                      <TableCell align="right">Department</TableCell> 
                      {/* I just realized. this new/revised 
                      isnt needed in an online app*/}
                      <TableCell align="right">Review</TableCell> 
                      <TableCell align="right">Training</TableCell> 
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableRow>
                      <TableCell component="th" scope="row">
                        {row.location}
                      </TableCell>
                      <TableCell align="right">{row.department}</TableCell>
                      <TableCell align="right">{row.review}</TableCell>
                      <TableCell align="right">{row.training}</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>


                <Typography variant="h6" gutterBottom component="div">
                  Steps
                </Typography>
                <Table size="small" aria-label="purchases">
                  <TableHead>
                    <TableRow>
                      <TableCell>Step #</TableCell>
                      <TableCell>Sequence of Job</TableCell>
                      <TableCell align="right">Potential Hazard</TableCell>
                      <TableCell align="right">Controls</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {console.log("yete: ")}
                    {/* {console.log(moreRows)} */}
                    {/* {moreRows.map((moreRow) => {if (moreRow.job == row.job){
                      {console.log('one moreRow at least')}
                      {console.log(moreRow.job)}(
                      <TableRow key={moreRow.id}>
                        <TableCell component="th" scope="row">
                          {moreRow.moreRow}
                        </TableCell>
                        <TableCell>{moreRow.seqJob}</TableCell>
                        <TableCell align="right">{moreRow.potHaz}</TableCell>
                        <TableCell align="right">{moreRow.control}</TableCell>
                      </TableRow>

                    )}})} */}
                  </TableBody>

                </Table>
                <Button variant="contained" color="secondary" onClick={() => {deleteClick(row.ID)}}>
                    Delete
                </Button>
                <Button variant="contained" color="primary" onClick={() => {routeChange(row.ID)}}>
                    Edit
                </Button>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </React.Fragment>
    );
  }


Row.propTypes = {
  row: PropTypes.shape({
    job: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    organization: PropTypes.string.isRequired,
    ppe: PropTypes.string.isRequired,
    ID: PropTypes.number.isRequired,
    selectedDate: PropTypes.string.isRequired,
    status: PropTypes.string.isRequired,
    supervisor: PropTypes.string.isRequired,
    analyst: PropTypes.string.isRequired,
    location: PropTypes.string.isRequired,
    department: PropTypes.string.isRequired,
    review: PropTypes.string.isRequired,
    training: PropTypes.string.isRequired,
    // extraInfo: PropTypes.arrayOf(
    //   PropTypes.shape({

    //   }),
    // ).isRequired,


    moreRow: PropTypes.arrayOf(
      PropTypes.shape({
        step: PropTypes.number.isRequired,
        seqJob: PropTypes.string.isRequired,
        potHaz: PropTypes.string.isRequired,
        control: PropTypes.string.isRequired
      }),
    ).isRequired,
  }).isRequired,
};


function Home() {
    const [fullResponse, setFullResponse] = useState([])
    const [jhaList, setJhaList] = useState([])
    const [doneFetch, setDoneFetch] = useState(false)
    // const [allSteps, setAllSteps] = useState([]);


    useEffect(async () => {
        setFullResponse(await api.getJha());
        setDoneFetch(true);
        if (doneFetch){
            console.log('Completed Fetch');
            // console.log(jhaList);
            setJhaList(fullResponse[0]['main']);
            console.log("stes")
            console.log(fullResponse[0].step)
            // setAllSteps(fullResponse[0].step);
        }
        // 

        console.log(jhaList);
